        _safeMint(_msgSender(), _mintAmount);
    }

    /// @notice Function to mint new tokens
    /// @param _mintAmount Amount of tokens to be minted
    function mint(
        uint256 _mintAmount
    ) public payable mintCompliance(_mintAmount) mintPriceCompliance(_mintAmount) {
